import React, { useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { Button } from '../components/button';
import { Card, CardContent } from '../components/card';
import { useHistory } from "react-router-dom"; 
import { addToCart, removeFromCart, clearCart, decrementFromCart } from '../store/cart';

const Checkout = () => {
  const [showPopup, setShowPopup] = useState(false);
  const cartItems = useSelector((state) => state.cart.items);
  const dispatch = useDispatch();
  const navigate = useHistory();

  const handleOrderClick = () => {
    setShowPopup(true);
    dispatch(clearCart());
  };

  const handleNavigateToProducts = () => {
    setShowPopup(false);
    navigate.push('/products');
  };

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-4">Checkout</h1>
      <div className="grid gap-4">
        {cartItems.map((item, index) => (
          <Card key={index}>
            <CardContent className="flex justify-between items-center p-4">
              <div>
                <h2 className="text-lg font-semibold">{item.name}</h2>
                <div className="flex items-center gap-2 mt-2">
                  <button
                    onClick={() => dispatch(decrementFromCart(item.id))}
                    className="px-2 py-1 bg-gray-200 rounded hover:bg-gray-300"
                  >
                    â€“
                  </button>
                  <span>{item.quantity}</span>
                  <button
                    onClick={() => dispatch(addToCart(item))}
                    className="px-2 py-1 bg-gray-200 rounded hover:bg-gray-300"
                  >
                    +
                  </button>
                </div>
              </div>
              <div className="flex items-center gap-4">
                <p className="font-medium">${item.price * item.quantity}</p>
                <Button variant="outline" onClick={() => dispatch(removeFromCart(item.id))}>Remove</Button>
              </div>
            </CardContent>
          </Card>
        ))}
      </div>

      {cartItems.length > 0 && (
        <div className="mt-6">
          <Button onClick={handleOrderClick}>Place Order</Button>
        </div>
      )}

      {showPopup && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white p-6 rounded-2xl shadow-lg max-w-sm w-full">
            <h2 className="text-xl font-bold mb-4">Order Confirmed!</h2>
            <p className="mb-4">Thank you for your purchase. Your order has been placed successfully.</p>
            <Button onClick={handleNavigateToProducts}>Back to Products</Button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Checkout;
