import React, { useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { Button } from '@/components/ui/button';
import { Card, CardContent } from '@/components/ui/card';
import { useHistory } from "react-router-dom"; 

// Redux slice actions for cart management
// Make sure your cartSlice.js looks like this:
//
// import { createSlice } from '@reduxjs/toolkit';
//
// const initialState = { items: [] };
//
// const cartSlice = createSlice({
//   name: 'cart',
//   initialState,
//   reducers: {
//     addToCart: (state, action) => {
//       const existing = state.items.find(item => item.id === action.payload.id);
//       if (existing) {
//         existing.quantity += 1;
//       } else {
//         state.items.push({ ...action.payload, quantity: 1 });
//       }
//     },
//     removeFromCart: (state, action) => {
//       state.items = state.items.filter(item => item.id !== action.payload);
//     },
//     clearCart: (state) => {
//       state.items = [];
//     },
//   },
// });
//
// export const { addToCart, removeFromCart, clearCart } = cartSlice.actions;
// export default cartSlice.reducer;

import { removeFromCart, clearCart } from '@/redux/cartSlice';

const Checkout = () => {
  const [showPopup, setShowPopup] = useState(false);
  const cartItems = useSelector((state) => state.cart.items);
  const dispatch = useDispatch();
  const navigate = useHistory();

  const handleOrderClick = () => {
    setShowPopup(true);
    dispatch(clearCart());
  };

  const handleNavigateToProducts = () => {
    setShowPopup(false);
    navigate.push('/products');
  };

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-4">Checkout</h1>
      <div className="grid gap-4">
        {cartItems.map((item, index) => (
          <Card key={index}>
            <CardContent className="flex justify-between items-center p-4">
              <div>
                <h2 className="text-lg font-semibold">{item.name}</h2>
                <p className="text-sm text-gray-500">Quantity: {item.quantity}</p>
              </div>
              <div className="flex items-center gap-4">
                <p className="font-medium">${item.price * item.quantity}</p>
                <Button variant="outline" onClick={() => dispatch(removeFromCart(item.id))}>Remove</Button>
              </div>
            </CardContent>
          </Card>
        ))}
      </div>
      {cartItems.length > 0 && (
        <div className="mt-6">
          <Button onClick={handleOrderClick}>Place Order</Button>
        </div>
      )}

      {showPopup && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white p-6 rounded-2xl shadow-lg max-w-sm w-full">
            <h2 className="text-xl font-bold mb-4">Order Confirmed!</h2>
            <p className="mb-4">Thank you for your purchase. Your order has been placed successfully.</p>
            <Button onClick={handleNavigateToProducts}>Back to Products</Button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Checkout;
