import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { addToCart } from '@/redux/cartSlice';
import { useHistory } from 'react-router-dom';
import { Button } from '@/components/ui/button';
import { Card, CardContent } from '@/components/ui/card';
import { ShoppingCart } from 'lucide-react'; // You can replace with any icon you prefer

const products = [
  { id: 1, name: 'T-Shirt', price: 25 },
  { id: 2, name: 'Hoodie', price: 50 },
  { id: 3, name: 'Cap', price: 15 },
];

const Products = () => {
  const dispatch = useDispatch();
  const navigate = useHistory();
  const cartItems = useSelector(state => state.cart.items);
  const totalCount = cartItems.reduce((sum, item) => sum + item.quantity, 0);

  return (
    <div className="p-6 relative">
      {/* Cart icon with count */}
      <div
        className="absolute top-4 left-4 flex items-center cursor-pointer"
        onClick={() => navigate('/checkout')}
      >
        <ShoppingCart className="w-6 h-6 text-gray-800" />
        <span className="ml-1 text-sm font-bold bg-red-500 text-white rounded-full w-5 h-5 flex items-center justify-center">
          {totalCount}
        </span>
      </div>

      <h1 className="text-2xl font-bold mb-6 text-center">Products</h1>
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-4">
        {products.map(product => (
          <Card key={product.id}>
            <CardContent className="p-4 flex flex-col justify-between h-full">
              <div>
                <h2 className="text-lg font-semibold">{product.name}</h2>
                <p className="text-sm text-gray-500">${product.price}</p>
              </div>
              <Button
                className="mt-4"
                onClick={() => dispatch(addToCart(product))}
              >
                Add to Cart
              </Button>
            </CardContent>
          </Card>
        ))}
      </div>
    </div>
  );
};

export default Products;
