import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { addToCart } from '../store/cart';
import { useHistory } from 'react-router-dom'; // Changed from useHistory to useNavigate
import { ShoppingCart } from 'lucide-react';

const Products = () => {
  const products = [
    { id: 1, name: 'T-Shirt', price: 25 },
    { id: 2, name: 'Hoodie', price: 50 },
    { id: 3, name: 'Cap', price: 15 },
  ];

  const dispatch = useDispatch();
  const navigate = useHistory(); // Correct hook for navigation in react-router-dom v6
  const cartItems = useSelector((state) => state.cart.items);
  const totalCount = cartItems.reduce((sum, item) => sum + item.quantity, 0);

  return (
    <div className="p-6 relative min-h-screen bg-gray-50">
      {/* Cart Icon */}
      <div
        className="absolute top-4 left-4 flex items-center gap-1 cursor-pointer"
        onClick={() => navigate('/checkout')} // Updated to use navigate
      >
        <ShoppingCart className="w-6 h-6 text-gray-800" />
        <span className="text-xs font-bold bg-red-500 text-white rounded-full w-5 h-5 flex items-center justify-center">
          {totalCount}
        </span>
      </div>

      <h1 className="text-2xl font-bold mb-6 text-center">Products</h1>
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-4">
        {products.map((product) => (
          <div
            key={product.id}
            className="bg-white shadow-md rounded-lg p-4 flex flex-col justify-between"
          >
            <div>
              <h2 className="text-lg font-semibold">{product.name}</h2>
              <p className="text-sm text-gray-500">${product.price}</p>
            </div>
            <button
              className="mt-4 bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
              onClick={() => dispatch(addToCart(product))}
            >
              Add to Cart
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Products;
